import React from "react";
import "./Alert.css";

interface AlertProps {
    message: string;
    isSuccess: boolean;
}

export const Alert = ({ message, isSuccess }: AlertProps) => {
    return (
        <div
            className={`alert alert__success d-flex justify-content-center flex-column align-items-center p-3 ${
                isSuccess ? "alert__success" : "alert__error"
                }`}
        >
            <h5 className="text-white bg-transparent">
                {isSuccess ? "Success!" : "Error!"}
            </h5>
            <div className="text-white bg-transparent text-center">{message}</div>
        </div>
    );
};

Alert.defaultProps = {
    isSuccess: false
}
